const port = process.env.PORT || 9090;

async function connectToWA() {
    console.log("CONNECTING KERM_MD-V1üß¨...");
    const { state, saveCreds } = await useMultiFileAuthState(__dirname + '/auth_info_baileys/');
    var { version } = await fetchLatestBaileysVersion();

    const conn = makeWASocket({
        logger: P({ level: 'silent' }),
        printQRInTerminal: false,
        browser: Browsers.macOS("Firefox"),
        syncFullHistory: true,
        auth: state,
        version
    });

    conn.ev.on('connection.update', (update) => {
        const { connection, lastDisconnect } = update;
        if (connection === 'close') {
            if (lastDisconnect.error.output.statusCode !== DisconnectReason.loggedOut) {
                connectToWA();
            }
        } else if (connection === 'open') {
            console.log('‚ôªÔ∏è INSTALLING PLUGINS FILES PLEASE WAIT... ü™Ñ');
            const path = require('path');
            fs.readdirSync("./plugins/").forEach((plugin) => {
                if (path.extname(plugin).toLowerCase() === ".js") {
                    require("./plugins/" + plugin);
                }
            });
            console.log('PLUGINS FILES INSTALL SUCCESSFULLY ‚úÖ');
            console.log('KERM_MD-V1 CONNECTED TO WHATSAPP ENJOY ‚úÖ');

            const up = `*KERM_MD-V1 CONNECTED SUCCESSFULLY ‚úÖ*\n\n*Prefix:* ${prefix}\n*Owner:* ${ownerNumber[0]}`;
            conn.sendMessage(conn.user.id, { text: up });
        }
    });

    conn.ev.on('creds.update', saveCreds);

    //============= READ STATUS ===========
    conn.ev.on('messages.upsert', async (mek) => {
        mek = mek.messages[0];
        if (!mek.message) return;
        mek.message = (getContentType(mek.message) === 'ephemeralMessage') ? mek.message.ephemeralMessage.message : mek.message;

        const m = sms(conn, mek);
        const type = getContentType(mek.message);
        const from = mek.key.remoteJid;
        const quoted = (type === 'extendedTextMessage' && mek.message.extendedTextMessage.contextInfo) ? mek.message.extendedTextMessage.contextInfo.quotedMessage : null;
        const body = (type === 'conversation') ? mek.message.conversation :
                    (type === 'extendedTextMessage') ? mek.message.extendedTextMessage.text :
                    (type === 'imageMessage' && mek.message.imageMessage.caption) ? mek.message.imageMessage.caption :
                    (type === 'videoMessage' && mek.message.videoMessage.caption) ? mek.message.videoMessage.caption : '';
        const sender = mek.key.fromMe ? (conn.user.id.split(':')[0] + '@s.whatsapp.net') : (mek.key.participant || mek.key.remoteJid);
        const senderNumber = sender.split('@')[0];
        const isGroup = from.endsWith('@g.us');
        const isOwner = ownerNumber.includes(senderNumber);

        const reply = (teks) => {
            conn.sendMessage(from, { text: teks }, { quoted: mek });
        };

        // === Fonctionnalit√© "save" vers le PM du bot lui-m√™me ===
        if (body.toLowerCase() === 'save' && quoted) {
            const savedMessage = quoted.conversation || quoted.extendedTextMessage?.text || "üîπ Message multim√©dia non textuel";

            // Envoi du message sauvegard√© dans le PM du bot lui-m√™me
            const botJid = conn.user.id.split(':')[0] + '@s.whatsapp.net';
            await conn.sendMessage(botJid, {
                text: `üíæ *Message sauvegard√© par ${senderNumber} :*\n\n"${savedMessage}"`,
            });

            reply("‚úÖ Message sauvegard√© et envoy√© dans le PM du bot.");
        }
    });
}

// Lancement de la connexion
connectToWA();

